import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const ReferralTickets = () => {
  const { slug } = useParams();
  const [isValidSlug, setIsValidSlug] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Validate slug via API or logic
    fetch(`http://127.0.0.1:8000/api/validate-slug/${slug}/`)
      .then((response) => {
        if (!response.ok) {
          setIsValidSlug(false);
        }
      })
      .catch(() => setIsValidSlug(false));
  }, [slug]);

  if (!isValidSlug) {
    navigate("/not-found"); // Redirect to NotFound
    return null;
  }

  return <div>{/* Existing ReferralTickets content */}</div>;
};
