import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./registration.css"; 
import { Helmet } from "react-helmet";

const TicketReferral = () => {
  const initialReferralState = {
    referrerName: "",
    name: "",
    email: "",
    mobile: "",
    nationality: "",
    how_many_members: "",
    have_pets: "",
  };

  const [formData, setFormData] = useState(initialReferralState);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://127.0.0.1:8000/api/ticket-referrals/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to submit ticket referral");
        }
        return response.json();
      })
      .then((data) => {
        toast.success("Ticket referral submitted successfully!", {
          position: "top-right",
        });
        setFormData(initialReferralState);
      })
      .catch((error) => {
        toast.error("Form submission failed. Please try again.", {
          position: "top-right",
        });
      });
  };

  return (
    <React.Fragment>
      <Helmet>
        <title>Refer Friends for Tickets to Pet Parents Fest 2025</title>
        <meta
          name="description"
          content="Invite your friends to the Pet Parents Fest 2025 by referring them to secure their free tickets!"
        />
      </Helmet>

      <div
        className="page-background"
        style={{
          backgroundImage: "url(/images/ticketReferral.webp)",
          minHeight: "100vh",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <div className="container">
          <section className="ticket-referral-section">
            <div className="text-left">
              <h6>Pet Parents Expo by Wondermom</h6>
              <h2>Refer Your Friends for Free Tickets!</h2>
              <p style={{ marginBottom: "15px", color: "#fff" }}>
                Invite your friends to the Pet Parents Expo on{" "}
                <strong>January 11th and 12th, 2024</strong>. Help them secure
                their tickets and enjoy the festival together! Fill in the
                details below to send your referral.
              </p>
            </div>
            <div className="contact-form ticket-referral-form">
              <form onSubmit={handleSubmit}>
                <h4 className="reg-title"> TICKET REFERRAL FORM </h4>
                <div className="row">
                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="referrerName"
                      type="text"
                      value={formData.referrerName}
                      onChange={handleChange}
                      placeholder="Your Name (Referrer) *"
                      required
                    />
                  </div>

                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="name"
                      type="text"
                      value={formData.name}
                      onChange={handleChange}
                      placeholder="Friend's Name *"
                      required
                    />
                  </div>

                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="email"
                      type="email"
                      value={formData.email}
                      onChange={handleChange}
                      placeholder="Friend's Email Address *"
                      required
                    />
                  </div>

                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="mobile"
                      type="text"
                      value={formData.mobile}
                      onChange={handleChange}
                      placeholder="Friend's Phone Number *"
                      required
                    />
                  </div>

                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="nationality"
                      type="text"
                      value={formData.nationality}
                      onChange={handleChange}
                      placeholder="Friend's Nationality *"
                      required
                    />
                  </div>
                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="how_many_members"
                      type="text"
                      value={formData.how_many_members}
                      onChange={handleChange}
                      placeholder="How Many Members Join *"
                      required
                    />
                  </div>
                  <div className="col-lg-12 col-sm-12">
                    <select
                      name="have_pets"
                      value={formData.have_pets}
                      onChange={handleChange}
                      required
                    >
                      <option value="" disabled>
                        Does your friend have pets? *
                      </option>
                      <option value="Yes">Yes</option>
                      <option value="No">No</option>
                    </select>
                  </div>

                  <div className="col-lg-12">
                    <button type="submit" className="main-button">
                      Submit Ticket Referral
                    </button>
                  </div>

                  <ToastContainer
                    closeButton={false}
                    autoClose={3000}
                    position="top-right"
                  />
                </div>
              </form>
            </div>
          </section>
        </div>
      </div>
    </React.Fragment>
  );
};

export default TicketReferral;
