import React, { useState, useEffect } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "../registration.css";
import { Helmet } from "react-helmet";
import { useParams } from "react-router-dom";

const TicketReferral = () => {
  // Get the slug from the URL using useParams
  const { slug } = useParams();

  // Initial form data state
  const initialTicketState = {
    referrerName: "",  // This will be populated from API
    name: "",
    email: "",
    mobile: "",
    nationality: "",
    how_many_members: "",
    have_pets: "",
  };

  const [formData, setFormData] = useState(initialTicketState);
  const [loading, setLoading] = useState(false); // To handle loading state
  const [referrerData, setReferrerData] = useState(null); // To store fetched referrer data
  const [isLoading, setIsLoading] = useState(true); // To indicate if the data is still loading

  // Fetch referrer data based on the slug
  useEffect(() => {
    // Make an API request to fetch the referrer data by slug using POST
    fetch("http://127.0.0.1:8000/api/referrals/", { // No slug in URL, post it in body
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ slug }) // Pass slug in the body of the request
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.referrerName) {
          setFormData((prevData) => ({
            ...prevData,
            referrerName: data.referrerName, // Set the referrerName from the response data
          }));
        } else {
          toast.error("Referrer name not found.", { position: "top-right" });
        }
      })
      .catch((error) => {
        toast.error("Failed to fetch referrer data. Please try again.", {
          position: "top-right",
        });
      })
      .finally(() => {
        setIsLoading(false); // Set isLoading to false once the data has been fetched
      });
  }, [slug]);

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Show loading state

    try {
      const response = await fetch(`http://127.0.0.1:8000/api/referrals/`, {
        method: "POST", // POST request to the specific slug-based URL
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error("Failed to submit referral");
      }

      const data = await response.json();
      toast.success("Referral submitted successfully!", {
        position: "top-right",
      });
      setFormData(initialTicketState); // Reset form
    } catch (error) {
      toast.error("Form submission failed. Please try again.", {
        position: "top-right",
      });
    } finally {
      setLoading(false); // Hide loading state
    }
  };

  // If data is still loading, render a loading state
  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <React.Fragment>
      <Helmet>
        <title>Get Your Free Tickets for the Best Pet Parents Fest 2025</title>
        <meta
          name="description"
          content="Secure your tickets for the Pet Parents Fest 2025! Enjoy live demos, expert tips, and exciting activities for dogs, cats, birds, and more!"
        />
      </Helmet>

      <div
        className="page-background"
        style={{
          backgroundImage: "url(/images/reg.webp)",
          minHeight: "100vh",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <div className="container">
          <section className="ticket-section">
            <div className="text-left">
              <h6>Pet Parents Expo by Wondermom</h6>
              <h2>Join Us for an Unforgettable Experience!</h2>
              <p style={{ marginBottom: "15px", color: "#fff" }}>
                We're excited to invite you to the Pet Parents Expo on{" "}
                <strong>January 11th and 12th, 2024</strong>! This event
                celebrates the joy of pets and brings together a community of
                pet lovers. Secure your spot today and explore exclusive
                opportunities for brands to connect with pet parents.
              </p>
              <p style={{ marginBottom: "15px", color: "#fff" }}>
                For any questions or assistance regarding your tickets, feel
                free to reach out to us at <strong>+971 54 508 3789</strong> or
                click here to{" "}
                <a href="https://wa.me/+971569066391">WhatsApp us</a>.
              </p>
            </div>

            <div className="contact-form ticket-referral-form">
              <form onSubmit={handleSubmit}>
                <h4 className="reg-title"> TICKET REFERRAL FORM </h4>
                <div className="row">
                  {/* Referrer Name (Pre-filled from slug data) */}
                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="referrerName"
                      type="text"
                      value={formData.referrerName || ""}
                      onChange={handleChange}
                      placeholder="Your Name (Referrer) *"
                      required
                      disabled={true} // Disabling the field to prevent user edits
                    />
                  </div>

                  {/* Friend's Name */}
                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="name"
                      type="text"
                      value={formData.name}
                      onChange={handleChange}
                      placeholder="Friend's Name *"
                      required
                    />
                  </div>

                  {/* Friend's Email */}
                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="email"
                      type="email"
                      value={formData.email}
                      onChange={handleChange}
                      placeholder="Friend's Email Address *"
                      required
                    />
                  </div>

                  {/* Friend's Mobile */}
                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="mobile"
                      type="text"
                      value={formData.mobile}
                      onChange={handleChange}
                      placeholder="Friend's Phone Number *"
                      required
                    />
                  </div>

                  {/* Friend's Nationality */}
                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="nationality"
                      type="text"
                      value={formData.nationality}
                      onChange={handleChange}
                      placeholder="Friend's Nationality *"
                      required
                    />
                  </div>

                  {/* How Many Members */}
                  <div className="col-lg-12 col-sm-12">
                    <input
                      name="how_many_members"
                      type="text"
                      value={formData.how_many_members}
                      onChange={handleChange}
                      placeholder="How Many Members Join *"
                      required
                    />
                  </div>

                  {/* Does your friend have pets? */}
                  <div className="col-lg-12 col-sm-12">
                    <select
                      name="have_pets"
                      value={formData.have_pets}
                      onChange={handleChange}
                      required
                    >
                      <option value="" disabled>
                        Does your friend have pets? *
                      </option>
                      <option value="Yes">Yes</option>
                      <option value="No">No</option>
                    </select>
                  </div>

                  {/* Submit Button */}
                  <div className="col-lg-12">
                    <button type="submit" className="main-button" disabled={loading}>
                      {loading ? "Submitting..." : "Submit Ticket Referral"}
                    </button>
                  </div>
                </div>
              </form>
            </div>
          </section>
        </div>
      </div>

      {/* Toast Notification */}
      <ToastContainer
        closeButton={false}
        position="top-right"
        autoClose={2000}
        hideProgressBar={true}
      />
    </React.Fragment>
  );
};

export default TicketReferral;
